import { Component, OnInit } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { catchError, Observable, of } from 'rxjs';
import { ErrorDialogComponent } from '../shared/components/error-dialog/error-dialog.component';

import { Course } from './model/course';
import { CoursesService } from './services/courses.service';

@Component({
    selector: 'app-courses',
    templateUrl: './courses.component.html',
    styleUrls: ['./courses.component.scss']
})

export class CoursesComponent implements OnInit {

    courses$: Observable<Course[]>;

    displayedColumns: string[];

    // coursesService: CoursesService;

    constructor(
        private coursesService: CoursesService,
        public dialog: MatDialog
    ) {

        // this.coursesService = new CourssesService();

        this.courses$ = this.coursesService.list()
            .pipe(
                catchError(error => {
                    this.onError('Erro ao tentar carregar Cursos...');
                    console.log(error);
                    return of([])
                })
            );

        this.displayedColumns = ['name', 'category'];

    }

    onError(errorMsg: string) {
        this.dialog.open(ErrorDialogComponent, {
            data: errorMsg
        });
    }

    ngOnInit() {
        // this.courses = this.coursesService.list();
    }

}
<mat-card>

    <mat-card-content>

        <mat-toolbar color="primary">
            Cursos Disponíveis
        </mat-toolbar>

        <div *ngIf="courses$ | async as courses; else loadingTemplate">

            <table mat-table [dataSource]="courses" class="mat-elevation-z8">

                <!--- Note that these columns can be defined in any order.
          The actual rendered columns are set as a property on the row definition" -->

                <!-- Name Column -->
                <ng-container matColumnDef="name">
                    <th mat-header-cell *matHeaderCellDef> Course </th>
                    <td mat-cell *matCellDef="let course"> {{ course.name }} </td>
                </ng-container>

                <!-- Category Column -->
                <ng-container matColumnDef="category">
                    <th mat-header-cell *matHeaderCellDef> Category </th>
                    <td mat-cell *matCellDef="let course">
                        {{ course.category }}
                        <mat-icon aria-hidden="false" aria-label="Categoria do Curso" fontIcon={{ course.category | category }}></mat-icon>
                    </td>
                </ng-container>

                <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
                <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table>

        </div>

        <ng-template #loadingTemplate>
            <div class="loading-spinner">
                <mat-spinner></mat-spinner>
            </div>
        </ng-template>

    </mat-card-content>

</mat-card>
